	(28, '2013-04-30 10:48:21', '2013-04-30 10:50:09', 26, 3, '#define REHASH(a, b, h) ((((h) - (a)*d) << 1) + (b))\n\nvoid KR(char *x, int m, char *y, int n) {\n   int d, hx, hy, i, j;\n\n   /* Preprocessing */\n   /* computes d = 2^(m-1) with\n      the left-shift operator */\n   for (d = i = 1; i < m; ++i)\n      d = (d<<1);\n\n   for (hy = hx = i = 0; i < m; ++i) {\n      hx = ((hx<<1) + x[i]);\n      hy = ((hy<<1) + y[i]);\n   }\n\n   /* Searching */\n   j = 0;\n   while (j <= n-m) {\n      if (hx == hy && memcmp(x, y + j, m) == 0)\n         OUTPUT(j);\n      hy = REHASH(y[j], y[j + m], hy);\n      ++j;\n   }\n\n}', 0),
	(29, '2013-04-30 10:50:17', '2013-04-30 10:54:00', 27, 3, 'int preSo(char *x, int m, unsigned int S[]) { \n    unsigned int j, lim; \n    int i; \n    for (i = 0; i < ASIZE; ++i) \n      S[i] = ~0; \n    for (lim = i = 0, j = 1; i < m; ++i, j <<= 1) { \n      S[x[i]] &= ~j; \n      lim |= j; \n    } \n    lim = ~(lim>>1); \n    return(lim); \n  } \n\n  void SO(char *x, int m, char *y, int n) { \n    unsigned int lim, state; \n    unsigned int S[ASIZE]; \n    int j; \n    if (m > WORD) \n      error("SO: Use pattern size <= word size"); \n\n    /* Preprocessing */ \n    lim = preSo(x, m, S); \n\n    /* Searching */ \n    for (state = ~0, j = 0; j < n; ++j) { \n      state = (state<<1) | S[y[j]]; \n      if (state < lim) \n        OUTPUT(j - m + 1); \n    } \n  } \n', 0),
	(30, '2013-04-30 10:55:18', '2013-04-30 10:56:49', 28, 3, 'void preMp(char *x, int m, int mpNext[]) {\n   int i, j;\n\n   i = 0;\n   j = mpNext[0] = -1;\n   while (i < m) {\n      while (j > -1 && x[i] != x[j])\n         j = mpNext[j];\n      mpNext[++i] = ++j;\n   }\n}\n\n\nvoid MP(char *x, int m, char *y, int n) {\n   int i, j, mpNext[XSIZE];\n\n   /* Preprocessing */\n   preMp(x, m, mpNext);\n\n   /* Searching */\n   i = j = 0;\n   while (j < n) {\n      while (i > -1 && x[i] != y[j])\n         i = mpNext[i];\n      i++;\n      j++;\n      if (i >= m) {\n         OUTPUT(j - i);\n         i = mpNext[i];\n      }\n   }\n}\n', 0),
	(31, '2013-04-30 10:58:26', '2013-04-30 18:33:24', 29, 3, 'void preKmp(char *x, int m, int kmpNext[]) {\n   int i, j;\n\n   i = 0;\n   j = kmpNext[0] = -1;\n   while (i < m) {\n      while (j > -1 && x[i] != x[j])\n         j = kmpNext[j];\n      i++;\n      j++;\n      if (x[i] == x[j])\n         kmpNext[i] = kmpNext[j];\n      else\n         kmpNext[i] = j;\n   }\n}\n\n\nvoid KMP(char *x, int m, char *y, int n) {\n   int i, j, kmpNext[XSIZE];\n\n   /* Preprocessing */\n   preKmp(x, m, kmpNext);\n\n   /* Searching */\n   i = j = 0;\n   while (j < n) {\n      while (i > -1 && x[i] != y[j])\n         i = kmpNext[i];\n      i++;\n      j++;\n      if (i >= m) {\n         OUTPUT(j - i);\n         i = kmpNext[i];\n      }\n   }\n}\n', 0),
	(32, '2013-04-30 11:01:23', '2013-04-30 11:04:44', 30, 3, 'int getTransition(char *x, int m, int p, List L[],\n                  char c) {\n   List cell;\n \n   if (p < m - 1 && x[p + 1] == c)\n      return(p + 1);\n   else if (p > -1) {\n      cell = L[p];\n      while (cell != NULL)\n         if (x[cell->element] == c)\n            return(cell->element);\n         else\n            cell = cell->next;\n      return(-1);\n   }\n   else\n      return(-1);\n}\n \n \nvoid setTransition(int p, int q, List L[]) {\n   List cell;\n \n   cell = (List)malloc(sizeof(struct _cell));\n   if (cell == NULL)\n      error("SIMON/setTransition");\n   cell->element = q;\n   cell->next = L[p];\n   L[p] = cell;\n}\n \n \nint preSimon(char *x, int m, List L[]) {\n   int i, k, ell;\n   List cell;\n \n   memset(L, NULL, (m - 2)*sizeof(List));\n   ell = -1;\n   for (i = 1; i < m; ++i) {\n      k = ell;\n      cell = (ell == -1 ? NULL : L[k]);\n      ell = -1;\n      if (x[i] == x[k + 1])\n         ell = k + 1;\n      else\n         setTransition(i - 1, k + 1, L);\n      while (cell != NULL) {\n         k = cell->element;\n         if (x[i] == x[k])\n            ell = k;\n         else\n            setTransition(i - 1, k, L);\n         cell = cell->next;\n      }\n   }\n   return(ell);\n}\n\n\nvoid SIMON(char *x, int m, char *y, int n) {\n   int j, ell, state;\n   List L[XSIZE];\n \n   /* Preprocessing */\n   ell = preSimon(x, m, L);\n \n   /* Searching */\n   for (state = -1, j = 0; j < n; ++j) {\n      state = getTransition(x, m, state, L, y[j]);\n      if (state >= m - 1) {\n         OUTPUT(j - m + 1);\n         state = ell;\n      }\n   }\n}', 0),
	(33, '2013-04-30 11:05:19', '2013-04-30 11:12:24', 31, 3, 'int preColussi(char *x, int m, int h[], int next[],\n               int shift[]) {\n   int i, k, nd, q, r, s;\n   int hmax[XSIZE], kmin[XSIZE], nhd0[XSIZE], rmin[XSIZE];\n\n   /* Computation of hmax */\n   i = k = 1;\n   do {\n      while (x[i] == x[i - k])\n         i++;\n      hmax[k] = i;\n      q = k + 1;\n      while (hmax[q - k] + k < i) {\n         hmax[q] = hmax[q - k] + k;\n         q++;\n      }\n      k = q;\n      if (k == i + 1)\n         i = k;\n   } while (k <= m);\n\n   /* Computation of kmin */\n   memset(kmin, 0, m*sizeof(int));\n   for (i = m; i >= 1; --i)\n      if (hmax[i] < m)\n         kmin[hmax[i]] = i;\n\n   /* Computation of rmin */\n   for (i = m - 1; i >= 0; --i) {\n      if (hmax[i + 1] == m)\n         r = i + 1;\n      if (kmin[i] == 0)\n         rmin[i] = r;\n      else\n         rmin[i] = 0;\n   }\n\n   /* Computation of h */\n   s = -1;\n   r = m;\n   for (i = 0; i < m; ++i)\n      if (kmin[i] == 0)\n         h[--r] = i;\n      else\n         h[++s] = i;\n   nd = s;\n \n   /* Computation of shift */\n   for (i = 0; i <= nd; ++i)\n      shift[i] = kmin[h[i]];\n   for (i = nd + 1; i < m; ++i)\n      shift[i] = rmin[h[i]];\n   shift[m] = rmin[0];\n\n   /* Computation of nhd0 */\n   s = 0;\n   for (i = 0; i < m; ++i) {\n      nhd0[i] = s;\n      if (kmin[i] > 0)\n         ++s;\n   }\n\n\n   /* Computation of next */\n   for (i = 0; i <= nd; ++i)\n      next[i] = nhd0[h[i] - kmin[h[i]]];\n   for (i = nd + 1; i < m; ++i)\n      next[i] = nhd0[m - rmin[h[i]]];\n   next[m] = nhd0[m - rmin[h[m - 1]]];\n\n   return(nd);\n}\n\nvoid COLUSSI(char *x, int m, char *y, int n) {\n   int i, j, last, nd,\n       h[XSIZE], next[XSIZE], shift[XSIZE];\n\n   /* Processing */\n   nd = preColussi(x, m, h, next, shift);\n\n   /* Searching */\n   i = j = 0;\n   last = -1;\n   while (j <= n - m) {\n      while (i < m && last < j + h[i] &&\n                      x[h[i]] == y[j + h[i]])\n         i++;\n      if (i >= m || last >= j + h[i]) {\n         OUTPUT(j);\n         i = m;\n      }\n      if (i > nd)\n         last = j + m - 1;\n      j += shift[i];\n      i = next[i];\n   }\n}', 0),
	(34, '2013-04-30 11:12:28', '2013-04-30 11:14:13', 32, 3, 'void GG(char *x, int m, char *y, int n) {\n   int i, j, k, ell, last, nd;\n   int h[XSIZE], next[XSIZE], shift[XSIZE];\n   char heavy;\n \n   for (ell = 0; x[ell] == x[ell + 1]; ell++);\n   if (ell == m - 1)\n      /* Searching for a power of a single character */\n      for (j = ell = 0; j < n; ++j)\n          if (x[0] == y[j]) {\n             ++ell;\n             if (ell >= m)\n                OUTPUT(j - m + 1);\n          }\n          else\n             ell = 0;\n   else {\n      /* Preprocessing */\n      nd = preCOLUSSI(x, m, h, next, shift);\n\n      /* Searching */\n      i = j = heavy = 0;\n      last = -1;\n      while (j <= n - m) {\n         if (heavy && i == 0) {\n            k = last - j + 1;\n            while (x[0] == y[j + k])\n               k++;\n            if (k <= ell || x[ell + 1] != y[j + k]) {\n               i = 0;\n               j += (k + 1);\n               last = j - 1;\n            }\n            else {\n               i = 1;\n               last = j + k;\n               j = last - (ell + 1);\n            }\n            heavy = 0;\n         }\n         else {\n            while (i < m && last < j + h[i] &&\n                            x[h[i]] == y[j + h[i]])\n               ++i;\n            if (i >= m || last >= j + h[i]) {\n               OUTPUT(j);\n               i = m;\n            }\n            if (i > nd)\n               last = j + m - 1;\n            j += shift[i];\n            i = next[i];\n         }\n         heavy = (j > last ? 0 : 1);\n      }\n   }\n}', 0),
	(35, '2013-04-30 11:14:55', '2013-04-30 11:22:53', 33, 3, 'void preKmp(char *x, int m, int kmpNext[]) {\n   int i, j;\n\n   i = 0;\n   j = kmpNext[0] = -1;\n   while (i < m) {\n      while (j > -1 && x[i] != x[j])\n         j = kmpNext[j];\n      i++;\n      j++;\n      if (x[i] == x[j])\n         kmpNext[i] = kmpNext[j];\n      else\n         kmpNext[i] = j;\n   }\n}\n\nvoid AXAMAC(char *x, int m, char *y, int n) {\n   int i, j, k, ell, kmpNext[XSIZE];\n\n   /* Preprocessing */\n   preKmp(x, m, kmpNext);\n   for (ell = 1; x[ell - 1] == x[ell]; ell++);\n   if (ell == m)\n      ell = 0;\n\n   /* Searching */\n   i = ell;\n   j = k = 0;\n   while (j <= n - m) {\n      while (i < m && x[i] == y[i + j])\n         ++i;\n      if (i >= m) {\n         while (k < ell && x[k] == y[j + k])\n            ++k;\n         if (k >= ell)\n            OUTPUT(j);\n      }\n      j += (i - kmpNext[i]);\n      if (i == ell)\n         k = MAX(0, k - 1);\n      else\n         if (kmpNext[i] <= ell) {\n            k = MAX(0, kmpNext[i]);\n            i = ell;\n         }\n         else {\n            k = ell;\n            i = kmpNext[i];\n         }\n   }\n}', 0),
