	(37, '2013-04-30 11:25:56', '2013-04-30 11:30:31', 35, 3, 'void preBmBc(char *x, int m, int bmBc[]) {\n   int i;\n \n   for (i = 0; i < ASIZE; ++i)\n      bmBc[i] = m;\n   for (i = 0; i < m - 1; ++i)\n      bmBc[x[i]] = m - i - 1;\n}\n \n \nvoid suffixes(char *x, int m, int *suff) {\n   int f, g, i;\n \n   suff[m - 1] = m;\n   g = m - 1;\n   for (i = m - 2; i >= 0; --i) {\n      if (i > g && suff[i + m - 1 - f] < i - g)\n         suff[i] = suff[i + m - 1 - f];\n      else {\n         if (i < g)\n            g = i;\n         f = i;\n         while (g >= 0 && x[g] == x[g + m - 1 - f])\n            --g;\n         suff[i] = f - g;\n      }\n   }\n}\n \nvoid preBmGs(char *x, int m, int bmGs[]) {\n   int i, j, suff[XSIZE];\n \n   suffixes(x, m, suff);\n \n   for (i = 0; i < m; ++i)\n      bmGs[i] = m;\n   j = 0;\n   for (i = m - 1; i >= 0; --i)\n      if (suff[i] == i + 1)\n         for (; j < m - 1 - i; ++j)\n            if (bmGs[j] == m)\n               bmGs[j] = m - 1 - i;\n   for (i = 0; i <= m - 2; ++i)\n      bmGs[m - 1 - suff[i]] = m - 1 - i;\n}\n \n \nvoid BM(char *x, int m, char *y, int n) {\n   int i, j, bmGs[XSIZE], bmBc[ASIZE];\n \n   /* Preprocessing */\n   preBmGs(x, m, bmGs);\n   preBmBc(x, m, bmBc);\n \n   /* Searching */\n   j = 0;\n   while (j <= n - m) {\n      for (i = m - 1; i >= 0 && x[i] == y[i + j]; --i);\n      if (i < 0) {\n         OUTPUT(j);\n         j += bmGs[0];\n      }\n      else\n         j += MAX(bmGs[i], bmBc[y[i + j]] - m + 1 + i);\n   }\n}\n   ', 0),
