	(55, '2013-05-01 09:53:24', '2013-05-01 09:54:28', 53, 3, 'void BNDM(char *x, int m, char *y, int n) {\n  int B[ASIZE];\n  int i, j, s, d, last;\n  if (m > WORD_SIZE)\n    error("BNDM");\n\n  /* Pre processing */\n  memset(B,0,ASIZE*sizeof(int));\n  s=1;\n  for (i=m-1; i>=0; i--){\n    B[x[i]] |= s;\n    s <<= 1;\n  }\n\n  /* Searching phase */\n  j=0;\n  while (j <= n-m){\n    i=m-1; last=m;\n    d = ~0;\n    while (i>=0 && d!=0) {\n      d &= B[y[j+i]];\n      i--;\n      if (d != 0){\n    if (i >= 0)\n      last = i+1;\n    else\n      OUTPUT(j);\n       }\n       d <<= 1;\n     }\n     j += last;\n   }\n  }', 0),
	(56, '2013-05-01 09:54:50', '2013-05-01 09:56:52', 54, 3, '#define FALSE      0\n#define TRUE       1\n\nint getTransition(char *x, int p, List L[], char c) {\n   List cell;\n\n   if (p > 0 && x[p - 1] == c)\n      return(p - 1);\n   else {\n      cell = L[p];\n      while (cell != NULL)\n         if (x[cell->element] == c)\n            return(cell->element);\n         else\n            cell = cell->next;\n      return(UNDEFINED);\n   }\n}\n\n\nvoid setTransition(int p, int q, List L[]) {\n   List cell;\n\n   cell = (List)malloc(sizeof(struct _cell));\n   if (cell == NULL)\n      error("BOM/setTransition");\n   cell->element = q;\n   cell->next = L[p];\n   L[p] = cell;\n}\n\n\nvoid oracle(char *x, int m, char T[], List L[]) {\n   int i, p, q;\n   int S[XSIZE + 1];\n   char c;\n\n   S[m] = m + 1;\n   for (i = m; i > 0; --i) {\n      c = x[i - 1];\n      p = S[i];\n      while (p <= m &&\n             (q = getTransition(x, p, L, c)) ==\n             UNDEFINED) {\n         setTransition(p, i - 1, L);\n         p = S[p];\n      }\n      S[i - 1] = (p == m + 1 ? m : q);\n   }\n   p = 0;\n   while (p <= m) {\n      T[p] = TRUE;\n      p = S[p];\n   }\n}\n\n\nvoid BOM(char *x, int m, char *y, int n) {\n   char T[XSIZE + 1];\n   List L[XSIZE + 1];\n   int i, j, p, period, q, shift;\n\n   /* Preprocessing */\n   memset(L, NULL, (m + 1)*sizeof(List));\n   memset(T, FALSE, (m + 1)*sizeof(char));\n   oracle(x, m, T, L);\n\n   /* Searching */\n   j = 0;\n   while (j <= n - m) {\n      i = m - 1;\n      p = m;\n      shift = m;\n      while (i + j >= 0 &&\n             (q = getTransition(x, p, L, y[i + j])) !=\n             UNDEFINED) {\n         p = q;\n         if (T[p] == TRUE) {\n            period = shift;\n            shift = i;\n         }\n         --i;\n      }\n      if (i < 0) {\n         OUTPUT(j);\n         shift = period;\n      }\n      j += shift;\n   }\n}', 0),
	(57, '2013-05-01 09:56:56', '2013-05-01 09:58:13', 55, 3, 'char *x, *y;\nint k, m, n, p, p1, p2, q, q1, q2, s;\n\n\nvoid search() {\n   while (p <= n - m) {\n      while (p + s + q < n && x[s + q] == y[p + s + q])\n         ++q;\n      if (q == m - s && memcmp(x, y + p, s + 1) == 0)\n         OUTPUT(p);\n      if (q == p1 + q1) {\n         p += p1;\n         q -= p1;\n      }\n      else {\n         p += (q/k + 1);\n         q = 0;\n      }\n   }\n}\n\n\nvoid parse() {\n   while (1) {\n      while (x[s + q1] == x[s + p1 + q1])\n         ++q1;\n      while (p1 + q1 >= k*p1) {\n         s += p1;\n         q1 -= p1;\n      }\n      p1 += (q1/k + 1);\n      q1 = 0;\n      if (p1 >= p2)\n         break;\n   }\n   newP1();\n}\n \n\nvoid newP2() {\n   while (x[s + q2] == x[s + p2 + q2] && p2 + q2 < k*p2)\n      ++q2;\n   if (p2 + q2 == k*p2)\n      parse();\n   else\n      if (s + p2 + q2 == m)\n         search();\n      else {\n         if (q2 == p1 + q1) {\n            p2 += p1;\n            q2 -= p1;\n         }\n         else {\n            p2 += (q2/k + 1);\n            q2 = 0;\n         }\n         newP2();\n      }\n}\n \n\nvoid newP1() {\n   while (x[s + q1] == x[s + p1 + q1])\n      ++q1;\n   if (p1 + q1 >= k*p1) {\n      p2 = q1;\n      q2 = 0;\n      newP2();\n   }\n   else {\n      if (s + p1 + q1 == m)\n         search();\n      else {\n         p1 += (q1/k + 1);\n         q1 = 0;\n         newP1();\n      }\n   }\n}\n\n\nvoid GS(char *argX, int argM, char *argY, int argN) {\n   x = argX;\n   m = argM;\n   y = argY;\n   n = argN;\n   k = 4;\n   p = q = s = q1 = p2 = q2 = 0;\n   p1 = 1;\n   newP1();\n}', 0),
	(58, '2013-05-01 09:58:25', '2013-05-01 10:00:46', 56, 3, '/* Computing of the maximal suffix for <= */\nint maxSuf(char *x, int m, int *p) {\n   int ms, j, k;\n   char a, b;\n\n   ms = -1;\n   j = 0;\n   k = *p = 1;\n   while (j + k < m) {\n      a = x[j + k];\n      b = x[ms + k];\n      if (a < b) {\n         j += k;\n         k = 1;\n         *p = j - ms;\n      }\n      else\n         if (a == b)\n            if (k != *p)\n               ++k;\n            else {\n               j += *p;\n               k = 1;\n            }\n         else { /* a > b */\n            ms = j;\n            j = ms + 1;\n            k = *p = 1;\n         }\n   }\n   return(ms);\n}\n \n/* Computing of the maximal suffix for >= */\nint maxSufTilde(char *x, int m, int *p) {\n   int ms, j, k;\n   char a, b;\n\n   ms = -1;\n   j = 0;\n   k = *p = 1;\n   while (j + k < m) {\n      a = x[j + k];\n      b = x[ms + k];\n      if (a > b) {\n         j += k;\n         k = 1;\n         *p = j - ms;\n      }\n      else\n         if (a == b)\n            if (k != *p)\n               ++k;\n            else {\n               j += *p;\n               k = 1;\n            }\n         else { /* a < b */\n            ms = j;\n            j = ms + 1;\n            k = *p = 1;\n         }\n   }\n   return(ms);\n}\n\n\n/* Two Way string matching algorithm. */\nvoid TW(char *x, int m, char *y, int n) {\n   int i, j, ell, memory, p, per, q;\n\n   /* Preprocessing */\n   i = maxSuf(x, m, &p);\n   j = maxSufTilde(x, m, &q);\n   if (i > j) {\n      ell = i;\n      per = p;\n   }\n   else {\n      ell = j;\n      per = q;\n   }\n\n   /* Searching */\n   if (memcmp(x, x + per, ell + 1) == 0) {\n      j = 0;\n      memory = -1;\n      while (j <= n - m) {\n         i = MAX(ell, memory) + 1;\n         while (i < m && x[i] == y[i + j])\n            ++i;\n         if (i >= m) {\n            i = ell;\n            while (i > memory && x[i] == y[i + j])\n               --i;\n            if (i <= memory)\n               OUTPUT(j);\n            j += per;\n            memory = m - per - 1;\n         }\n         else {\n            j += (i - ell);\n            memory = -1;\n         }\n      }\n   }\n   else {\n      per = MAX(ell + 1, m - ell - 1) + 1;\n      j = 0;\n      while (j <= n - m) {\n         i = ell + 1;\n         while (i < m && x[i] == y[i + j])\n            ++i;\n         if (i >= m) {\n            i = ell;\n            while (i >= 0 && x[i] == y[i + j])\n               --i;\n            if (i < 0)\n               OUTPUT(j);\n            j += per;\n         }\n         else\n            j += (i - ell);\n      }\n   }\n}', 0),
	(59, '2013-05-01 10:00:50', '2013-05-01 10:04:09', 57, 3, '/* Compute the next maximal suffix. */\nvoid nextMaximalSuffix(char *x, int m,\n                       int *i, int *j, int *k, int *p) {\n   char a, b;\n \n   while (*j + *k < m) {\n      a = x[*i + *k];\n      b = x[*j + *k];\n      if (a == b)\n         if (*k == *p) {\n            (*j) += *p;\n            *k = 1;\n         }\n         else\n            ++(*k);\n      else\n         if (a > b) {\n            (*j) += *k;\n            *k = 1;\n            *p = *j - *i;\n         }\n         else {\n            *i = *j;\n            ++(*j);\n            *k = *p = 1;\n         }\n   }\n}\n \n \n/* String matching on ordered alphabets algorithm. */\nvoid SMOA(char *x, int m, char *y, int n) {\n   int i, ip, j, jp, k, p;\n \n   /* Searching */\n   ip = -1;\n   i = j = jp = 0;\n   k = p = 1;\n   while (j <= n - m) {\n      while (i + j < n && i < m && x[i] == y[i + j])\n         ++i;\n      if (i == 0) {\n         ++j;\n         ip = -1;\n         jp = 0;\n         k = p = 1;\n      }\n      else {\n         if (i >= m)\n            OUTPUT(j);\n         nextMaximalSuffix(y + j, i+1, &ip, &jp, &k, &p);\n         if (ip < 0 ||\n             (ip < p &&\n              memcmp(y + j, y + j + p, ip + 1) == 0)) {\n            j += p;\n            i -= p;\n            if (i < 0)\n               i = 0;\n            if (jp - ip > p)\n               jp -= p;\n            else {\n               ip = -1;\n               jp = 0;\n               k = p = 1;\n            }\n         }\n         else {\n            j += (MAX(ip + 1,\n                      MIN(i - ip - 1, jp + 1)) + 1);\n            i = jp = 0;\n            ip = -1;\n            k = p = 1;\n         }\n      }\n   }\n}\n', 0),
