	(75, '2013-05-01 10:54:40', '2013-05-01 10:55:14', 67, 3, 'void stoogesort(int *item, int left,int right)\n{\n   register int tmp, k;\n   if(item[left]>item[right])\n   {\n      tmp=item[left];\n      item[left]=item[right];\n      item[right]=tmp;\n   }\n   if((left+1)>=right)\n        return;\n \n   k=(int)((right-left+1)/3);\n   stoogesort(item,left, right-k);\n   stoogesort(item, left+k, right);\n   stoogesort(item, left, right-k);\n}', 0),
	(76, '2013-05-01 10:56:03', '2013-05-01 10:56:56', 68, 38, 'procedure Antialiasing(Canvas: TCanvas; const AX1, AY1, AX2, AY2: real; const linecolor: TColor);\nvar\n  swapped: boolean;\n  procedure plot(const x, y, c: real);\n  var\n    resclr: TColor;\n  begin\n    if swapped then\n      resclr := Canvas.Pixels[round(y), round(x)]\n    else\n      resclr := Canvas.Pixels[round(x), round(y)];\n    resclr := RGB(round(GetRValue(resclr) * (1-c) + GetRValue(LineColor) * c),\n                  round(GetGValue(resclr) * (1-c) + GetGValue(LineColor) * c),\n                  round(GetBValue(resclr) * (1-c) + GetBValue(LineColor) * c));\n    if swapped then\n      Canvas.Pixels[round(y), round(x)] := resclr\n    else\n      Canvas.Pixels[round(x), round(y)] := resclr;\n  end;\n \n  function rfrac(const x: real): real;\n  begin\n    rfrac := 1 - frac(x);\n  end;\n \n  procedure swap(var a, b: real);\n  var\n    tmp: real;\n  begin\n    tmp := a;\n    a := b;\n    b := tmp;\n  end;\nvar\n  x1, x2, y1, y2, dx, dy, gradient, xend, yend, xgap, xpxl1, ypxl1,\n  xpxl2, ypxl2, intery: real;\n  x: integer;\nbegin\n  x1 := AX1;\n  x2 := AX2;\n  y1 := AY1;\n  y2 := AY2;\n \n  dx := x2 - x1;\n  dy := y2 - y1;\n  swapped := abs(dx) < abs(dy);\n  if swapped then\n  begin\n    swap(x1, y1);\n    swap(x2, y2);\n    swap(dx, dy);\n  end;\n  if x2 < x1 then\n  begin\n    swap(x1, x2);\n    swap(y1, y2);\n  end;\n \n  gradient := dy / dx;\n \n  xend := round(x1);\n  yend := y1 + gradient * (xend - x1);\n  xgap := rfrac(x1 + 0.5);\n  xpxl1 := xend;\n  ypxl1 := floor(yend);\n  plot(xpxl1, ypxl1, rfrac(yend) * xgap);\n  plot(xpxl1, ypxl1 + 1, frac(yend) * xgap);\n  intery := yend + gradient;\n \n  xend := round(x2);\n  yend := y2 + gradient * (xend - x2);\n  xgap := frac(x2 + 0.5);\n  xpxl2 := xend;\n  ypxl2 := floor(yend);\n  plot(xpxl2, ypxl2, rfrac(yend) * xgap);\n  plot(xpxl2, ypxl2 + 1, frac(yend) * xgap);\n \n  for x := round(xpxl1) + 1 to round(xpxl2) - 1 do\n  begin\n    plot(x, floor(intery), rfrac(intery));\n    plot(x, floor(intery) + 1, frac(intery));\n    intery := intery + gradient;\n  end;\nend;', 0),
	(77, '2013-05-01 10:57:32', '2013-05-01 10:58:35', 69, 4, 'void drawLine(int x1, int y1, int x2, int y2) {\n    const int deltaX = abs(x2 - x1);\n    const int deltaY = abs(y2 - y1);\n    const int signX = x1 < x2 ? 1 : -1;\n    const int signY = y1 < y2 ? 1 : -1;\n    //\n    int error = deltaX - deltaY;\n    //\n    setPixel(x2, y2);\n    while(x1 != x2 || y1 != y2) {\n        setPixel(x1, y1);\n        const int error2 = error * 2;\n        //\n        if(error2 > -deltaY) {\n            error -= deltaY;\n            x1 += signX;\n        }\n        if(error2 < deltaX) {\n            error += deltaX;\n            y1 += signY;\n        }\n    }\n \n}', 0),
	(78, '2013-05-01 10:58:41', '2013-05-01 11:00:02', 69, 5, 'private int sign (int x) {\n        return (x > 0) ? 1 : (x < 0) ? -1 : 0;\n}\n \npublic void drawBresenhamLine (int xstart, int ystart, int xend, int yend, Graphics g)\n{\n        int x, y, dx, dy, incx, incy, pdx, pdy, es, el, err;\n \n        dx = xend - xstart;\n        dy = yend - ystart;\n \n        incx = sign(dx);\n        incy = sign(dy);\n \n        if (dx < 0) dx = -dx;\n        if (dy < 0) dy = -dy;\n \n        if (dx > dy)\n        {\n                pdx = incx;     pdy = 0;\n                es = dy;        el = dx;\n        }\n        else\n        {\n                pdx = 0;        pdy = incy;\n                es = dx;        el = dy;\n        }\n \n        x = xstart;\n        y = ystart;\n        err = el/2;\n        g.drawLine (x, y, x, y);\n        \n        for (int t = 0; t < el; t++)\n        {\n                err -= es;\n                if (err < 0)\n                {\n                        err += el;\n                        x += incx;\n                        y += incy;\n                }\n                else\n                {\n                        x += pdx;\n                        y += pdy;\n                }\n \n                g.drawLine (x, y, x, y);\n        }\n}', 0),
	(79, '2013-05-01 11:02:35', '2013-05-01 11:03:14', 70, 38, 'program atkin;\nvar is_prime:array[1..10001] of boolean; jj:int64;\nprocedure dosieve(limit:int64);\nvar i,k,x,y:int64; n:int64;\nbegin\n  for i:=5 to limit do\n    is_prime[i]:=false;\n  for x:=1 to trunc(sqrt(limit)) do\n    for y:=1 to trunc(sqrt(limit)) do\n    begin\n      n:=4*sqr(x)+sqr(y);\n      if (n<=limit) and ((n mod 12 = 1) or (n mod 12 = 5)) then\n        is_prime[n]:=not is_prime[n];\n      n:=n-sqr(x);\n      if (n<=limit) and (n mod 12 = 7) then\n        is_prime[n]:=not is_prime[n];\n      n:=n-2*sqr(y);\n      if (x>y) and (n<=limit) and (n mod 12 = 11) then\n        is_prime[n]:=not is_prime[n];\n    end;\n  for i:=5 to trunc(sqrt(limit)) do\n  begin\n    if is_prime[i] then\n    begin\n      k:=sqr(i);\n      n:=k;\n      while n<=limit do\n      begin\n        is_prime[n]:=false;\n        n:=n+k;\n      end;\n    end;\n  end;\n  is_prime[2]:=true;\n  is_prime[3]:=true;\nend;\nbegin\n  dosieve(10000);\n  for jj:=1 to 10000 do\n    if is_prime[jj] then\n      writeln(jj);\nend.', 0),
	(80, '2013-05-01 23:16:00', '2013-05-01 23:18:36', 71, 2, 'def get_primes(N):\n    prime = [1] * (N + 1)\n    prime[0] = prime[1] = 0\n    for i in xrange(2, N + 1):\n        if prime[i] and i * i < N:\n            for j in xrange(i * i, N + 1, i):\n                prime[j] = 0\n    return [i for i, p in enumerate(prime) if p]', 0),
	(82, '2013-05-01 23:43:59', '2013-05-01 23:46:03', 73, 2, 'def get_primes_and_factorization(N):\n    lp = [0] * N\n    pr = []\n    for i in xrange(2, N):\n        if not lp[i]:\n            lp[i] = i\n            pr.append(i)\n        for j, pr_j in enumerate(pr):\n            if pr_j <= lp[i] and i * pr_j <= N - 1:\n                lp[i * pr_j] = pr_j\n            else:\n                break\n    return pr, lp', 0),
