INSERT INTO `catalog_catalogitemmodel` (`id`, `name`, `category_id`, `purpose`, `action`, `usage`, `active_substance`, `chemical`, `image_id`, `description_image_id`, `orderindex`, `show_on_main_page`, `image_small_id`) VALUES
	(1, 'Дозреватель', 4, '<p>для томатов открытого и защищенного грунта.</p>', '<p>- ускоряет созревание бурых, незрелых плодов, увеличивает их массу;<br />- ускоряет рост еще зеленых плодов;<br />- повышает сборы спелых и крупных плодов;<br />- полностью сохраняет урожай до наступления фитофтороза, возникающего из-за воздействия&nbsp;холодных туманов и низких ночных температур;<br />- позволяет резко уменьшить или вообще исключить наличие зеленых плодов в сборе. Этот прием экономически выгоден весной, когда цены на продукцию&nbsp;высоки и при ликвидации оборота.</p>', '<p>Содержимое флакона растворить в воде, тщательно перемешать и немедленно использовать.<br />Тепличные томаты опрыскивать по листьям однократно при созревании 5-30% плодов примерно за 2-3 недели до окончательного сбора. Томаты открытого грунта опрыскивать при созревании 5-20% плодов.&nbsp;<br />Не рекомендуется обрабатывать растения томата раньше указанных сроков, т.к. это может привести к преждевременному старению растений.</p>\r\n<p>Препорат выпускается в упаковках различной емкости: ампула 1,7 мл, флаконы 3,5 мл, 7 мл, 14 мл, 35 мл, 70мл.</p>', 'д.в.', '2-хлорэтилфосфоновая\r\nкислота - 629 г/л', 157, 217, 1, 1, NULL),
	(2, 'Корнеплодное Осеннее', 12, '<p>Для внекорневой подкормки свеклы и моркови в период интенсивного роста корнеплода (август). Обеспечивает растения питательными элементами, которые усиливают рост корнеплода и повышают его сахаристость.</p>', '<p>- содержит все основные питательные макро- и&nbsp;микроэлементы, соотношение которых удовлетворяет специфические потребности культуры&nbsp;моркови и свеклы;<br />- усиливает рост корнеплодов;<br />- повышает урожайность и сахаристость корнеплодов;<br />- улучшает лежкость корнеплодов.</p>', '<p>Рекомендуется проводить 3 подкормки через 10 дней, начиная с августа &ndash; период интенсивного роста корнеплода. Содержимое пакета 4 г растворить в 2 л воды. Опрыскивание производить утром или вечером. Расход &ndash; 0,3 л/ 10 м2. Рекомендуется подкормки сочетать с замачиванием семян перед посевом в растворе в течение 24 часов.</p>', 'в %', ' В - 3,5,  Mn - 14,  К - 12\r\n', 199, 141, 2, 1, NULL),
	(3, 'Овощное для лука', 12, '<p>для подкормки лука.</p>', '<p>- содержит все основные питательные макро- и&nbsp;микроэлементы, соотношение которых удовлетворяет специфические потребности лука;&nbsp;<br />- усиливает рост;<br />- повышает урожайность лука.</p>', '<p>Содержимое пакета (20 г) растворить в 10 литрах&nbsp;воды, удобрить после полива водой.&nbsp;<br />Рекомендуется проводить две подкормки: первую - после отрастания листьев, вторую - в начале завязывания луковицы.&nbsp;</p>', 'в %', 'N:P:К - 13:18:20 + микроэлементы +\r\nMgO + гуминовые кислоты - 1,3', 15, 136, 3, 1, NULL),
	(4, 'Овощное               для чеснока                    ', 12, '<p>для подкормки чеснока.</p>', '<p>- содержит все основные питательные макро- и микроэлементы, соотношение которых удовлетворяет специфические потребности культуры чеснока;<br />- усиливает рост;<br />- улучшает лежкость;<br />- обеспечивает существенные прибавки урожая.</p>', '<p>Рекомендуются не более двух подкормок:</p>\r\n<p>первая - после отрастания листьев,</p>\r\n<p>вторая - в начале завязывания луковицы.</p>\r\n<p>Содержимое пакета (20 г) растворить в 10 литрах воды, удобрить после полива водой.</p>', 'в %', 'N:P:К - 13:18:20 + микроэлементы + MgO + гуминовые кислоты - 1,5\r\n\r\n', 13, 136, 0, 1, NULL),
	(5, 'Живые цветы', 3, '<p>для продления жизни срезанных цветов и сохранения их декоративных качеств в букете.<br />&laquo;Живые цветы&raquo; применяются для лилий, пионов, циний и др. цветов.</p>', '<p>- обеспечивает цветы питательными веществами;<br />- содержит антимикробные агенты, которые предотвращают процесс гниения в воде;<br />- улучшает водный баланс;<br />- способствует нормальному развитию цветов;<br />- продлевает срок сохранности срезанных цветов в букете до 10-28 дней в зависимости от вида цветов и температуры окружающей среды;<br />- предотвращает быстрое увядание и старение лепестков.</p>', '<p>Обрезать стебли под водой острым ножом наискось. Удалить нижние листья, чтобы они не погружались в воду. Содержимое пакета растворить в 1 литре отстоянной воды комнатной температуры, налить раствор в вазу так, чтобы стебли были погружены не более, чем на 8-10 см. Раствор не менять, один раз&nbsp;в 1-3 дня по мере испарения доливать свежий раствор.</p>', 'в %', 'сахароза - 98; микроэлементы -2', 149, 126, 0, 1, NULL),
	(6, 'Живой букет', 3, '<p>для продления жизни срезанных цветов и сохранения их декоративных качеств в букете.<br />&laquo;Живой букет&raquo; подходит для гвоздик, гладиолусов, гербер, ирисов и других цветов.<br /><br /></p>', '<p>- обеспечивает цветы питательными веществами;<br />- содержит антимикробные агенты, которые предотвращают процесс гниения в воде;<br />- улучшает водный баланс;<br />- способствует нормальному развитию цветов;<br />- продлевает срок сохранности срезанных цветов в букете до 10-28 дней в зависимости от вида цветов и температуры окружающей среды;<br />- предотвращает быстрое увядание и старение лепестков.</p>', '<p>Обрезать стебли под водой острым ножом наискось. Удалить нижние листья, чтобы они не погружались в воду. Содержимое пакета растворить в 1 литре отстоянной воды комнатной температуры, налить раствор в вазу так, чтобы стебли были погружены не более, чем на 8-10 см. Раствор не менять, один раз в 1-3 дня по мере испарения доливать свежий раствор.</p>', 'в %', 'сахароза - 99; микроэлементы - 1\r\n\r\n', 150, 148, 0, 1, NULL),
	(7, 'ОберегЪ', 5, '<p>для предпосевной обработки семян, клубней, луковиц, надземной части вегетирующих растений.</p>', '<p>- стимулирует рост и развитие растений;<br />- повышает иммунитет растений к грибным и бактериальным заболеваниям на всех стадиях роста;<br />- предупреждает возникновение заболеваний растений;<br />- повышает адаптацию к неблагоприятным условиям внешней среды и стрессам;<br />- увеличивает урожайность различных культур до 30% за счет образования системной устойчивости;<br />- улучшает качество урожая;<br />- снижает инфекционный фон почвы и воздуха.<br />Эффект от обработки сохраняется в течение месяца.</p>', '<p>Содержимое ампулы растворить в 5 л воды или 5 капель на 0,5 л и тщательно перемешать. Замачивание семян в течение 1 часа (огурец 30-40 мин.), опрыскивание клубней, луковиц, надземной части вегетирующих растений в фазе 3-х настоящих листьев в начале цветения (бутонизации) и через 30 дней после первой обработки.&nbsp;<br />Приготовленный раствор использовать в течение 1-1,5 часов.</p>', 'д.в.', 'арахидоновая кислота - 0,15 г/л', 153, NULL, 0, 1, NULL),
	(8, 'Проросток', 5, '<p>для предпосевной обработки семян, клубней и луковиц плодовых, овощных и декоративных культур.</p>', '<p>- повышает энергию прорастания семян, улучшает их всхожесть;<br />- ускоряет ростовую активность проростков;<br />- активизирует иммунитет на стадии прорастания семян;<br />- повышает устойчивость рассады к заболеваниям, резким сменам температуры, недостатку влаги и&nbsp;другим стрессам;<br />- увеличивает урожайность и выход ранней продукции.<br />Эффект от обработки сохраняется в течение месяца.</p>', '<p>Содержимое ампулы растворить в 0,5 л воды или 10 капель на 100 мл воды и тщательно перемешать.&nbsp;<br />Приготовленный раствор использовать в течение 1-1,5 часов.<br />Предпосевная обработка семян:<br />Замачивание семян: огурец - в течение 30-40 мин.; <br />томат, капуста, морковь - в течение 1 часа; <br />лук - в течение 0,5-1 часа. Расход - 2 мл/г.<br />Горох - опрыскивание семян. Расход - 100 мл/10 кг.<br />Предпосадочная обработка клубней и луковиц:<br />картофель - расход - 1 л/100 кг;<br />лук-севок - расход - 7 мл/кг.</p>', 'д.в.', 'арахидоновая кислота – 0,015 г/л\r\n\r\n', 154, 163, 0, 1, NULL),
	(9, 'Завязь универсальная', 6, '<p>для применения на овощных и плодово-ягодных культурах.</p>', '<p>- стимулирует образование завязей и их рост при&nbsp;неблагоприятных погодных условиях и отсутствии опыляющих насекомых;<br />- предотвращает опадение цветков и молодых завязей;<br />- сокращает сроки созревания на 5-7 дней;<br />- увеличивает ранний и общий урожай на 15-30 %;<br />- обеспечивает повышение качества продукции;<br />- повышает устойчивость к заболеваниям и весенним заморозкам.</p>', '<p>Растворить препарат в воде и тщательно перемешать.&nbsp;<br />Опрыскивания проводят утром или вечером в сухую безветренную погоду, равномерно смачивая листья.&nbsp;<br />Рабочий раствор хранят не более 1 суток.<br />Томаты опрыскивают 3 раза: в начале цветения 1, 2-ой и 3-ей кисти. Площадь обработки - 20-50 м&sup2;. <br />Перцы и баклажаны опрыскивают 2 раза: в фазу бутонизации и начала цветения.&nbsp;<br />Огурцы открытого и защищенного грунта обрабатывают 2 раза: в фазах начала цветения и в период массового цветения (1,4 г препарата на 1 л воды). Площадь обработки 14-17 м&sup2;.&nbsp;<br />Виноград опрыскивают в конце цветения. Расход 1500 л/га.</p>', 'д.в.', 'гиббереллиновых кислот\r\nнатриевые соли - 5,5 г/кг\r\n\r\n', 155, 213, 0, 1, NULL),
	(10, 'Завязь для огурцов', 6, '<p>для огурцов, кабачков и тыквы открытого и защищенного грунта.</p>', '<p>- стимулирует плодообразование даже при неблагоприятных условиях и отсутствии пчел;<br />- уменьшает опадение цветков и молодых завязей;<br />- ускоряет рост и созревание плодов огурца;<br />- повышает ранний и общий урожай на 15-30%;<br />- повышает устойчивость к заболеваниям и неблагоприятным погодным условиям.</p>', '<p>Растворить 2 г препарата в 1,4 л воды. Опрыскивание производить в фазах начала цветения (появления единичных цветков) и массового цветения. Кратность применения: 2 раза.&nbsp;<br />Использовать рабочий раствор в день приготовления. Опрыскивание производить утром или вечером в сухую безветренную погоду.&nbsp;</p>', 'д.в.', 'гиббереллиновых кислот \r\nнатриевые соли - 5,5 г/кг', 156, 218, 0, 1, NULL),
	(11, 'Томатон', 6, '<p>для томатов открытого и защищенного грунта.</p>', '<p>- стимулирует завязывание плодов при неблагоприятных условиях (особенно в жару);<br />- предотвращает опадение завязей;<br />- ускоряет рост и созревание плодов;<br />- увеличивает ранний урожай на 50-100% в зависимости от сорта и условий выращивания, а общий урожай не менее чем на 30%;&nbsp;<br />- повышает качество выращенных плодов.</p>', '<p>Содержимое ампулы растворить в 0,5 л воды, добавить 1-2 капли любого моющего средства, тщательно перемешать. Обработать 1-ю и 2-ю цветочные кисти однократно, обмакивая их в раствор или опрыскивая, когда не менее половины цветков раскрыто в утренние или вечерние часы.<br /> Препарат &laquo;Томатон&raquo; <br /> <span style="font-family: \'arial black\', \'avant garde\';">Экономичен</span> - обрабатываются только новые соцветия!<br /> <span style="font-family: \'arial black\', \'avant garde\';">Удобен</span> - неиспользованный раствор можно хранить весь сезон и применять по мере появления соцветий!<br /> <span style="font-family: \'arial black\', \'avant garde\';">Эффективен</span> - пустоцветов нет, а урожай созревает на 7-10 дней раньше!</p>', 'д.в.', '4-хлорфеноксиуксусная \r\nкислота - 2,5 г/л', 152, 135, 0, 1, NULL),
	(12, 'Ортон Рост', 12, '<p>для подкормки всех культур на протяжении всего вегетационного периода.</p>', '<p>- обеспечивает растения азотом, фосфором, калием,микроэлементами, улучшает их усвоение;<br />- ускоряет рост и развитие растений;<br />- повышает устойчивость к неблагоприятным условиям;&nbsp;<br />- повышает урожай плодов и овощей.<br /><br /></p>', '<p>Содержимое пакета (20 г) растворить в 10 литрах воды. Подкармливать 3-4 раза за сезон, а рассаду и цветочно-декоративные культуры - каждые 2 недели. Плодовые и декоративные многолетники подкармливают: 1-ый - в период распускания листьев; 2-ой - перед цветением; последующие с интервалом в 15-20 дней. В период массового цветения для стимуляции плодообразования рекомендуется обработать соцветия препаратом "Завязь".</p>', 'в %', 'N:P2О5:К2О - 10:5:20 +  микро-\r\nэлементы, гуминовые кислоты - 3,5\r\n\r\n', 138, 137, 0, 1, NULL),
	(13, 'Микро-Фe', 11, '<p>для корневой и некорневой подкормки растений.</p>', '<p>- активизирует фотосинтез;<br />- предотвращает появление хлороза и дефицита микроэлементов;<br />- способствует повышению урожая и его качества;<br />- повышает устойчивость растений к заболеваниям и неблагоприятным условиям среды.</p>', '<p>Растворить 5 г препарата в 5 л воды. Опрыскивание&nbsp;растений производить на ранней стадии развития (3-4&nbsp;настоящих листа) и стадии интенсивного роста утром или вечером в сухую безветренную погоду, равномерно смачивая поверхность листьев. Кратность обработки: 2 раза.</p>', 'д.в.', 'железо в хелатной форме, бор,\r\nкобальт, марганец, цинк, молибден.', 139, 140, 0, 1, NULL),
	(14, 'Цветочное для комнатных декоративно-лиственных растений', 12, '<p>для корневой подкормки комнатных декоративно-лиственных растений.</p>', '<p>- обеспечивает растения макро- и микроэлементами для роста крупных, красивых листьев;<br />- способствует развитию декоративных качеств комнатных цветов этой группы;<br />- улучшает усвоение питательных элементов растениями и благоприятно влияет на их рост.</p>', '<p>Содержимое пакета (4 г) растворить в 2 литрах&nbsp;воды. Подкормку проводят по влажной почве&nbsp;весной и летом не реже 1 раза в 2 недели,&nbsp;осенью и зимой - 1 раз в 2 месяца.<br />Расход раствора - в зависимости от размера растения.</p>', 'в %', 'N:P2О5:K2О - 19:6:20 + MgO - 1,5\r\n+ микроэлементы + \r\nгуминовые  кислоты - 1,8', 144, 145, 0, 1, NULL),
	(15, 'Гумат', 12, '<p>для подкормки овощных, плодово-ягодных и декоративных культур.</p>', '<p>- повышает устойчивость к неблагоприятным условиям&nbsp;и заболеваниям;<br />- ускоряет прорастание семян, рост и развитие растений;<br />- повышает урожайность;<br />- способствует лучшему укоренению и приживаемости;<br />- улучшает усвоение минерального питания;<br />- повышает качество продукции.</p>', '<p>Содержимое пакета (10 г) растворить в 200 мл горячей воды. Для замачивания семян 1 ст. ложку раствора разводят в 5 л воды, а для полива и опрыскивания - в 10 л воды.&nbsp;<br />Семена томатов замачивают на трое суток, капусты, баклажанов - 2 суток, огурцов и цветочно-декоративных культур - 1 сутки. Черенки замачивают на 1 сутки, погружая их в раствор на 1/3 высоты.&nbsp;<br />Полив и опрыскивание растений проводят в период распускания листьев, в период цветения и далее через 2 недели, утром или вечером в сухую безветренную погоду. Норма расхода для полива - 6 л/м&sup2;; при опрыскивании - 3 л/100 м&sup2;. Для плодовых норма расхода при поливе 1-2&nbsp;ведра на саженец или молодой кустарник, до 4-8 ведер &ndash; на взрослое дерево или кустарник,&nbsp;при опрыскивании - 8-10 л/100 м&sup2;. Оставшийся&nbsp;раствор используют при компостировании.</p>', 'д.в.', 'содержание гуминовых кислот \r\nне менее 40%', 146, 131, 0, 1, NULL),
	(19, 'Живая роза', 3, '<p>для продления жизни срезанных цветов и сохранения их декоративных качеств в букете.</p>', '<p>- обеспечивает розы питательными веществами;<br />- улучшает водный баланс и предотвращает процесс гниения в воде;<br />- предотвращает быстрое увядание и старение лепестков;<br />- продлевает срок жизни срезанных роз до 2-3 недель;<br />- сохраняет декоративные качества роз в букете.</p>', '<p>Обрезать стебли под водой острым ножом наискось. Удалить нижние листья, чтобы они не погружались в воду. Содержимое пакета (30 г) растворить в 1 литре отстоянной воды комнатной температуры, налить раствор в вазу так, чтобы стебли были погружены не более, чем на 8-10 см. Раствор не менять, один раз в&nbsp;1-3 дня по мере испарения доливать свежий раствор.</p>', 'в %', 'сахароза - 97; микроэлементы - 3\r\n', 151, 148, 0, 1, NULL),
	(20, 'Завязь для томатов', 6, '<p>для применения в открытом и защищенном грунте на томате, баклажане, перце.</p>', '<p>- стимулирует образование завязей и их рост даже при неблагоприятных погодных условиях и&nbsp;отсутствии опыляющих насекомых;<br />- уменьшает опадение цветков и завязей;<br />- ускоряет рост и созревание плодов;<br />- увеличивает ранний и общий урожай на 15-30%;<br />- обеспечивает повышение качества продукции;<br />- повышает устойчивость к заболеваниям и неблагоприятным погодным условиям.</p>', '<p>Растворить 2 г препарата в 1 л воды. Опрыскивание растений производить в начале цветения 1, 2 и&nbsp;3 кистей.<br />Кратность применения 3 раза.<br />Использовать рабочий раствор в день приготовления. Опрыскивание производить утром или вечером в сухую безветренную погоду.</p>', 'д.в.', 'гиббереллиновых кислот \r\nнатриевые соли, 5,5 г/кг', 158, 219, 0, 1, NULL),
	(21, 'Завязь Овощная', 6, '<p>для овощных культур открытого и защищенного грунта: томатов, огурцов, баклажан, перца, фасоли, картофеля, гороха, капусты.</p>', '<p>- стимулирует образование завязей и их рост при&nbsp;неблагоприятных погодных условиях и отсутствии опыляющих насекомых;<br />- предотвращает опадение цветков и молодых завязей;<br />- сокращает сроки созревания на 5-7 дней;<br />- увеличивает ранний и общий урожай на 15-30 %;<br />- обеспечивает повышение качества продукции;<br />- повышает устойчивость к заболеваниям и неблагоприятным погодным условиям.</p>', '<p>Растворить 10 г препарата в необходимом количестве воды: 5 л - томаты, перец, 7 л - огурцы, фасоль, капуста, 10 л - картофель, 15 л - горох. Использовать рабочий&nbsp;раствор в день приготовления. Опрыскивание производить утром или вечером в сухую безветренную погоду.</p>', 'д.в.', 'гиббереллиновых кислот \r\nнатриевые соли, 5,5 г/кг', 159, 212, 0, 1, NULL),
	(22, 'Завязь для капусты', 6, '<p>для применения в открытом и защищенном грунте на капусте ранних и поздних сортов.</p>', '<p>- стимулирует завязывание кочана;<br />- ускоряет рост кочана, увеличивает его плотность, размер и вес;<br />- улучшает качество продукции;<br />- увеличивает ранний и общий урожай на 15-30%;<br />- повышает устойчивость к заболеваниям и &nbsp;неблагоприятным погодным условиям.</p>', '<p>Капуста поздняя:<br />Растворить 2 г препарата в 1,4 л воды. Опрыскивание производить в фазах 6-8 листьев и начала завязывания кочана. Кратность применения 2.<br />Капуста ранняя:<br />Опрыскивание производить в фазах 6-8 листьев, начала завязывания кочана и через 10 дней после&nbsp;второго применения. Кратность применения 3.<br />Использовать рабочий раствор в день приготовления. Опрыскивание производить утром или вечером в сухую безветренную погоду.</p>', 'д.в.', ' гиббереллиновых кислот \r\n натриевые соли, 5,5 г/кг', 162, 220, 0, 1, NULL),
	(23, 'Завязь плодовая', 6, '<p>для плодовых культур: яблони, груши, сливы, вишни.</p>', '<p>- стимулирует образование завязей и их рост при&nbsp;неблагоприятных погодных условиях и отсутствии опыляющих насекомых;<br />- предотвращает опадение цветков и молодых завязей;<br />- сокращает сроки созревания на 5-7 дней;<br />- увеличивает ранний и общий урожай на 15-30 %;<br />- обеспечивает повышение качества продукции;<br />- повышает устойчивость к заболеваниям и неблагоприятным погодным условиям.</p>', '<p>Растворить 10 г препарата в 5 л воды. Опрыскивание производить в фазу массового цветения и повторно после опадения лепестков.<br />Использовать рабочий раствор в день приготовления. Опрыскивание производить утром или вечером в сухую безветренную погоду.</p>', 'д.в.', 'гиббереллиновых кислот \r\nнатриевые соли, 5,5 г/кг', 161, 214, 0, 1, NULL),
	(24, 'УкоренитЪ', 7, '<p>для черенкования, посадки саженцев, пересадки деревьев и кустарников.</p>', '<p>- стимулирует образование новых корней;<br />- ускоряет рост и развитие мощной корневой системы с большим количеством корневых волосков;<br />- предотвращает загнивание черенков или заражение поврежденных при пересадке корней саженцев;<br />- обеспечивает лучшую приживаемость растений при черенковании, посадке саженцев, пересадке деревьев и кустарников за счет быстрого восстановления корневой системы.</p>', '<p>Смоченные водой черенки встряхнуть и погрузить в препарат на глубину 1-1,5 см, затем сразу высадить в почву.&nbsp;<br />При посадке саженцев корневую систему замачивают в течение 6 часов в суспензии препарата: растворяют его в небольшом количестве кипящей воды и доводят водой до объема 10 л (норма расхода 1 г/л).&nbsp;<br />При пересадке культур - полив под корень однократно через 10 дней после высадки. Расход раствора в <br />зависимости от размера и возраста растения.</p>', 'д.в.', 'индолилмасляная кислота – 5 г/кг', 178, NULL, 0, 1, NULL),
	(25, 'Гетероауксин', 7, '<p>для стимуляции корнеобразования и укоренения черенков, саженцев различных культур.</p>', '<p>- стимулирует корнеобразование и укоренение черенков и саженцев плодовых, ягодных, и декоративных культур;<br />- повышает приживаемость рассады;<br />- улучшает срастание тканей при прививке;<br />- оказывает стимулирующее действие на продолжи тельность цветения.</p>', '<p>Рассада овощных и однолетних цветочных культур - обмакивание корневой системы перед высадкой.<br />Прививка винограда - обмакивание нижней части привоя и верхней части подвоя перед прививкой в раствор в течение 2-3 сек. Расход препарата 20-30 г/л воды, расход рабочего раствора - 1л/ 1000 штук.<br />Пересадка сеянцев и саженцев плодовых и ягодных культур - обмакивание корневой системы перед посадкой и последующий полив растений через 5 дней после их высадки в грунт. Расход препарата 10 г/10 л, расход рабочего раствора - зависит от величины растения.&nbsp;<br />Черенкование плодовых, ягодных и декоративных культур - замачивание полуодревесневших черенков в течение 10-16 ч., зеленых черенков роз - в течение 16 ч. Расход препарата 4 г/л воды, расход рабочей жидкости - 1л/50 штук (1л/100 штук - для роз).<br />Луковичные и клубнелуковичные культуры - замачивание луковиц и клубней на 16-20 часов. Расход препарата 2 г/л воды, расход рабочего раствора - 1л/0,5 кг.</p>', 'д.в.', 'индолил-3-уксусной кислоты \r\nкалиевая соль - 50 г/кг\r\n\r\n', 202, 203, 0, 1, NULL);
INSERT INTO `catalog_catalognewsmodel` (`id`, `name`, `image_id`, `short_description`, `description`, `date_added`, `author_id`) VALUES
	(2, 'Новый стимулятор дозревания для томатов - "Дозреватель"', 168, '     Культура томата была и остается одной из самых важных и любимых у многих овощеводов и фермеров. Различают два основных периода развития плода томата.Различают два основных периода развития плода томата. В первый период идут усиленный рост плодов и формирование семян. Во второй период развития изменяются морфологические и биохимические признаки плода: он размягчается, приобретает свойственные ему окраску, вкус и аромат, т.е. достигает потребительской спелости.', '<div>\r\n<div>\r\n<div>\r\n<div>\r\n<div class="news-item news-detail">\r\n<div class="news-detail">\r\n<p>&nbsp; &nbsp; &nbsp;Культура томата была и остается одной из самых важных и любимых у многих овощеводов и фермеров. Различают два основных периода развития плода томата. В первый период идут усиленный рост плодов и формирование семян.</p>\r\n<p>&nbsp; &nbsp; &nbsp;Во второй период развития изменяются морфологические и биохимические признаки плода: он размягчается, приобретает свойственные ему окраску, вкус и аромат, т.е. достигает потребительской спелости. При созревании в плодах томата образуется ростовое вещество, которое обеспечивает их созревание, происходит синтез органических веществ, вследствие чего в плодах повышается содержание сахара, витамина С и сухого вещества, уменьшается содержание кислот. Созревшие плоды приобретают характерную окраску в результате образования в них пигментов.</p>\r\n<p>&nbsp; &nbsp; &nbsp;Однако в период интенсивного созревания плодов томата в открытом грунте или в необогреваемых пленочных теплицах возможно значительное ухудшение погодных условий: уменьшается количество света, повышается влажность воздуха, часто возникает угроза заморозков, низких ночных температур, холодных туманов и дождей. При этом и в самих плодах замедляется процесс образования ростового вещества, которое обеспечивает созревание плодов. Из-за нехватки ростовых веществ зеленые плоды не растут, вовремя не созревают, могут чернеть и гнить, т.к. неблагоприятные погодные условия в период созревания способствуют развитию фитофтороза, мучнистой росы и др.</p>\r\n<p>&nbsp; &nbsp; &nbsp;Научными сотрудниками компании &laquo;Ортон&raquo; был разработан и зарегистрирован новый препарат <strong>&laquo;Дозреватель&raquo;</strong> на основе природного ростового вещества, стимулирующего созревание плодов и входящего в группу этиленпродуцентов. Влияние действующего вещества препарата <strong>&laquo;Дозреватель&raquo;</strong> на процессы созревания обусловлено его способностью ускорять процессы созревания плодов и усиливать их окраску.</p>\r\n<p>Препарат <strong>&laquo;Дозреватель&raquo;</strong> применяют на культуре томата открытого и защищенного грунта для повышения дружности созревания томатов и ускорения созревания в зависимости от цели:</p>\r\n<p>- для получения раннего урожая, когда 5-15% плодов на кисти красные или розовые</p>\r\n<p>- при последнем сборе плодов томата в открытом грунте за 6-10 дней, когда ночная температура опускается и возникает опасность заболевания растений фитофторозом</p>\r\n<p>- при ликвидации оборота тепличной культуры томата за 10-14 дней до окончательного сбора урожая, когда 5-30% плодов красные или розовые</p>\r\n<p>&nbsp; &nbsp; &nbsp;Однократная обработка (опрыскивание) растений томата препаратом <strong>&laquo;Дозреватель&raquo;</strong> ускоряет созревание бурых, незрелых плодов, увеличивает их массу, одновременно ускоряет рост еще зеленых плодов. Обработка препаратом позволяет резко уменьшить или вообще исключить наличие зеленых плодов в сборе. Этот прием экономически выгоден весной, когда цены на продукцию высоки и при ликвидации оборота, что позволяет полностью сохранить урожай.</p>\r\n<p>&nbsp; &nbsp; &nbsp;По результатам испытаний обработка растений томата в условиях защищенного грунта перед ликвидацией оборота способствовала ускорению созревания плодов, выход урожая красных плодов возрос в 3,5 раза, доля зеленых плодов была значительно ниже, чем в контроле (более подробную информацию в&nbsp;фотоотчете 2010г. часть 10).</p>\r\n<p><em>&nbsp; &nbsp; <strong>&nbsp;В ООО &laquo;Надежда 2&raquo; Камызякского района Астраханской области</strong></em> проводились производственные испытания нового препарата <strong>&laquo;Дозреватель&raquo;</strong> на культуре томата открытого грунта. Через 10 дней после применения препарата <strong>&laquo;Дозреватель&raquo;</strong> отмечено созревание и покраснение почти 100 % плодов, облегчение отделения плодов от материнского растения, что позволило применить комбайновую уборку и полностью сохранить урожай.</p>\r\n<p>&nbsp; &nbsp; &nbsp;Разработана технология применения препарата &laquo;Дозреватель&raquo; для управления процессом созревания томатов. Плоды томатов часто убирают недозрелыми для повышения транспортабельности и лёжкости, но зеленые плоды не имеют вкуса, аромата и др., т.е. соответствующих потребительских качеств. После обработки препаратом &laquo;Дозреватель&raquo; плоды приобретают характерный вкус и внешний вид. Длительность обработки зависит от степени зрелости плодов.</p>\r\n<p>&nbsp; &nbsp; &nbsp;Таким образом, препарат <strong>&laquo;Дозреватель&raquo;</strong> дает возможность производителю получать ранний и поздний (при ликвидации оборота) более высокий урожай зрелых, красных плодов томата, позволяет повысить экономическую эффективность выращивания этой ценной овощной культуры и получить дополнительную прибыль</p>\r\n<p style="text-align: center;"><span style="text-decoration: underline;"><strong>На сегодняшний день аналогов препарату "Дозреватель" нет!&nbsp;</strong></span></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>', '2012-12-17 16:08:52', 2),
	(3, 'Испытания новых препаратов в 2011г.', 166, 'Время летит очень быстро. Снова пришла весна, затем лето, и опять начинается пора испытаний уже известных препаратов в различных климатических зонах нашей страны, а также новых препаратов в нашей теплице.', '<div id="page-wrapper">\r\n<div id="content-wrapper">\r\n<div id="content">\r\n<div id="workarea">\r\n<div class="news-item news-detail">\r\n<div class="news-detail">\r\n<p>&nbsp; &nbsp; &nbsp;Время летит очень быстро. Снова пришла весна, затем лето, и опять начинается пора испытаний уже известных препаратов в различных климатических зонах нашей страны, а также новых препаратов в нашей теплице. В этом году будем испытывать регуляторы роста на рассаде различных культур (огурец, свекла, петрушка, салат и капуста). &nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Рассада свеклы. Обработка начинается на самых ранних стадиях развития.</p>\r\n<p>&nbsp;<img src="http://orton.ru/upload/medialibrary/443/4437fc8dbd8d08ea830548a84fad631b.JPG" alt="свекла" width="200" height="150" /> &nbsp; &nbsp;&nbsp;<img src="http://orton.ru/upload/medialibrary/2d2/2d2a5c56bbb16b4872af403ab69000b5.JPG" alt="рассада свеклы" width="113" height="150" /></p>\r\n<p>Рассада огурца.&nbsp;</p>\r\n<p><img onmouseover="this.src=\'http://orton.ru/upload/medialibrary/6f4/6f4645cdd00772d832c0594610986fba.JPG\';" src="http://orton.ru/upload/medialibrary/6f4/6f4645cdd00772d832c0594610986fba.JPG" alt="рассада огурца" width="200" height="150" />&nbsp; &nbsp; &nbsp;<img src="http://orton.ru/upload/medialibrary/2a2/2a25a3c1c515df24bd4d11bde4ee515a.JPG" alt="огурцы" width="200" height="150" />&nbsp; &nbsp; &nbsp;<img src="http://orton.ru/upload/medialibrary/408/408fd7cd06f959f48ec6e397494c778e.JPG" alt="огурцы" width="113" height="150" /></p>\r\n<p>Рассада капусты.</p>\r\n<p><img src="http://orton.ru/upload/medialibrary/774/7742f408747de242677d5499220ffb70.JPG" alt="рассада капусты" width="200" height="150" />&nbsp; &nbsp; &nbsp;<img src="http://orton.ru/upload/medialibrary/713/713cda316669380356b965ff7164d528.JPG" alt="капуста" width="113" height="150" /></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>', '2012-12-17 19:10:32', 2),
	(4, 'Международная выставка на ВВЦ «ЦВЕТЫ-FLOWERS - 2011»', 167, 'С 31 августа по  03 сентября 2011 на ВВЦ проходила XVIIІ международная выставка цветов, растений, оборудования и материалов для декоративного садоводства и цветочного бизнеса \r\n                                             «ЦВЕТЫ-FLOWERS - 2011»\r\nВыставка "Цветы" - самая крупная международная профессиональная выставка по цветоводству и декоративному садоводству для профессионалов и представителей цветочного бизнеса, проводимая в России. Ежегодно в выставке принимают участие свыше 700 компаний, в том числе и наша фирма, из 30 стран мира.\r\n', '<div>\r\n<div>\r\n<div>\r\n<div>\r\n<div class="news-item news-detail">\r\n<div class="news-detail">\r\n<p>&nbsp; &nbsp; &nbsp;С 31 августа по 03 сентября 2011 на ВВЦ проходила XVIIІ международная выставка цветов, растений, оборудования и материалов для декоративного садоводства и цветочного бизнеса</p>\r\n<p style="text-align: center;"><strong>&laquo;ЦВЕТЫ-FLOWERS - 2011&raquo;</strong></p>\r\n<p><strong>&nbsp; &nbsp; &nbsp; Выставка "Цветы"</strong> - самая крупная международная профессиональная выставка по цветоводству и декоративному садоводству для профессионалов и представителей цветочного бизнеса, проводимая в России. Ежегодно в выставке принимают участие свыше 700 компаний, в том числе и наша фирма, из 30 стран мира. Выставку посещает более 50 тысяч человек, из них около 40 тысяч специалистов и представителей бизнеса из России и зарубежных стран.</p>\r\n<p><strong>&nbsp; &nbsp; &nbsp; Среди посетителей выставки</strong>: руководители высшего звена, директора, владельцы и специалисты компаний, производящих и реализующих цветочную продукцию, растения, машины, оборудование, сопутствующую продукцию, предприятия оптовой и розничной торговли, представители всех российских регионов - от Крайнего севера до Юга. Наряду с профессионалами выставку посещает большое число любителей.</p>\r\n<p>&nbsp; &nbsp; &nbsp;Для нас выставка прошла весьма плодотворно: были встречи со старыми партнерами, новые знакомства и перспективные предложения для дальнейшей совместной работы. Около нашего стенда постоянно толпились посетители, поскольку наша продукция хорошо известна, а новинки (например, препарат &laquo;Дозреватель&raquo;) вызывают интерес не только у любителей, но и у профессионалов.</p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>', '2012-12-17 19:14:14', 2),
	(5, 'Наши препараты теперь и в шоу-боксах.', 171, 'Наши препараты теперь и в шоу-боксах, использование которых позволит эффективно организовать выкладку на полке и увеличить продажи товара.', '<div id="page-wrapper">\r\n<div id="content-wrapper">\r\n<div id="content">\r\n<div id="workarea">\r\n<div class="news-item news-detail">\r\n<div class="news-detail">Наши препараты теперь и в шоу-боксах, использование которых позволит эффективно организовать выкладку на полке и увеличить продажи товара. На лицевой стороне короба &mdash; изображение упаковки препарата, размер шоу-бокса соответствует стандартам торговых полок магазинов, верхняя часть бокса отрывается по линии перфорации.</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>', '2012-12-17 19:15:22', 2),
	(6, 'Отчет о поисковых испытаниях (теплица на Клязьме) 2011г.', 172, 'В 2011 году провели предварительные испытания для разработки нового препарата на основе растительных природных веществ. Объектом испытаний служила рассада овощных культур: огурцы гибрид Гармонист, капуста сорт Московская поздняя, свекла Кестрел, петрушка.', '<div>\r\n<div>\r\n<div>\r\n<div>\r\n<div class="news-item news-detail">\r\n<div class="news-detail">\r\n<div align="center">\r\n<p align="center"><strong>ОТЧЕТ</strong></p>\r\n<p style="text-align: left;">&nbsp; &nbsp; &nbsp;В 2011 году провели предварительные испытания для разработки нового препарата на основе растительных природных веществ. Объектом испытаний служила рассада овощных культур: огурцы гибрид Гармонист, капуста сорт Московская поздняя, свекла Кестрел, петрушка.</p>\r\n<p style="text-align: left;">&nbsp; &nbsp; &nbsp;Посев семян всех культур осуществляли в кюветы 13 мая. Пикировку проводили с 20 по 25 мая в кассеты по 28 шт. (огурец) и по 40 шт. (остальные). Опыт проводился на фоне засухи. На фотографиях хорошо видно, как отличаются растения по вариантам.</p>\r\n<p style="text-align: left;">&nbsp; &nbsp; &nbsp;После проведенных обработок и наблюдений можно сделать следующие предварительные выводы: новый препарат представляет интерес для дальнейшей работы. Вероятная активность &ndash; антистрессовая, в данном случае &ndash; при повышенных температурах выращивания и засухе.</p>\r\n<p style="text-align: left;">&nbsp; &nbsp; &nbsp;<img src="http://orton.ru/upload/medialibrary/8de/8de5cb5ba4f30f11d4614b606ced902b.JPG" alt="рассада" width="200" height="150" />&nbsp; &nbsp; &nbsp;<img src="http://orton.ru/upload/medialibrary/54e/54e3688ace394c0933143280f481389e.JPG" alt="рассада" width="200" height="150" /> &nbsp; &nbsp;&nbsp;<img src="http://orton.ru/upload/medialibrary/0e2/0e2aee2e45268d7eb979a47f1d4b92ea.JPG" alt="рассада" width="200" height="150" /> &nbsp; &nbsp;&nbsp;<img src="http://orton.ru/upload/medialibrary/0d0/0d0f5e9ec50bec279d399730091ab90d.JPG" alt="рассада" width="200" height="150" /></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n</div>', '2011-08-26 16:39:56', 2);
